import { getRequestList } from '@/api/audit';
import { getUserList } from '@/api/users';
import { formatJson } from '@/utils';
export default {
  name: 'Requestinfor',
  data: function data() {
    return {
      // 遮罩层
      loading: true,
      // 选中数组
      ids: [],
      // 非多个禁用
      multiple: true,
      // 查询用户
      username: '',
      // 总条数
      total: 0,
      // 表格数据
      list: [],
      // 用户数据
      userList: [],
      // 状态数据字典
      statusOptions: [],
      // 查询参数
      queryParams: {
        page: 1,
        page_size: 10,
        user_id: undefined,
        remote_ip: undefined
      },
      start_date: '',
      end_date: '',
      pickerOptions: {
        disabledDate: function disabledDate(time) {
          return time.getTime() > Date.now();
        },
        shortcuts: [{
          text: '今天',
          onClick: function onClick(picker) {
            picker.$emit('pick', new Date());
          }
        }, {
          text: '昨天',
          onClick: function onClick(picker) {
            var date = new Date();
            date.setTime(date.getTime() - 3600 * 1000 * 24);
            picker.$emit('pick', date);
          }
        }, {
          text: '一周前',
          onClick: function onClick(picker) {
            var date = new Date();
            date.setTime(date.getTime() - 3600 * 1000 * 24 * 7);
            picker.$emit('pick', date);
          }
        }]
      }
    };
  },
  created: function created() {
    this.getUser();
    this.getList(); // this.getDicts('sys_common_status').then(response => {
    //     this.statusOptions = response.data
    // })
  },
  methods: {
    /** 查询数据库变更记录 */
    getList: function getList() {
      var _this = this;

      this.loading = true;
      getRequestList(this.queryParams).then(function (response) {
        _this.list = response.results;

        for (var i in _this.list) {
          _this.list[i]['num'] = parseInt(i) + 1;
        }

        _this.total = response.count;
        _this.loading = false;
      });
    },

    /** 获取用户列表 */
    getUser: function getUser() {
      var _this2 = this;

      getUserList().then(function (response) {
        _this2.userList = response.results;
      });
    },

    /** 搜索按钮操作 */
    handleQuery: function handleQuery() {
      this.queryParams.page = 1;
      this.queryParams.user_id = undefined;

      for (var i in this.userList) {
        if (this.username === this.userList[i]['username']) {
          this.queryParams.user_id = this.userList[i]['id'];
        }
      }

      this.queryParams.event_type = this.event_type_value;
      this.getList();
    },

    /** 重置按钮操作 */
    resetQuery: function resetQuery() {
      this.username = '';
      this.queryParams.remote_ip = '';
      this.queryParams.user_id = '';
      this.handleQuery();
    },

    /** 转换用户id状态为用户名 */
    formatuserID: function formatuserID(row, column) {
      var val = '';

      for (var i in this.userList) {
        if (row.user_id === this.userList[i]['id']) {
          val = this.userList[i]['username'];
        }
      }

      return val;
    },

    /** 导出按钮操作 */
    handleExport: function handleExport() {
      var _this3 = this;

      // const queryParams = this.queryParams
      this.$confirm('是否确认导出API请求数据表单?', '警告', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        _this3.downloadLoading = true;
        import('@/utils/Export2Excel').then(function (excel) {
          var tHeader = ['编号', 'id', '请求相对路径', '请求方法', '请求参数', '提交数据', '请求IP地址', '操作时间', '用户ID', '用户名'];
          var filterVal = ['num', 'id', 'url', 'remote_ip', 'method', 'query_string', 'post_data', 'datetime', 'user_id', 'username'];

          for (var i in _this3.list) {
            for (var j in _this3.userList) {
              if (_this3.list[i]['user_id'] === _this3.userList[j]['id']) {
                _this3.list[i]['username'] = _this3.userList[j]['username'];
              }
            }
          }

          var list = _this3.list;
          var data = formatJson(filterVal, list);
          excel.export_json_to_excel({
            header: tHeader,
            data: data,
            filename: 'API请求日志',
            autoWidth: true,
            // Optional
            bookType: 'xlsx' // Optional

          });
          _this3.downloadLoading = false;
        });
      });
    }
  }
};
