import { getLoginList } from '@/api/audit';
import { formatJson } from '@/utils';
export default {
  name: 'Logininfor',
  data: function data() {
    return {
      // 遮罩层
      loading: true,
      // 选中数组
      ids: [],
      // 非多个禁用
      multiple: true,
      // 总条数
      total: 0,
      // 表格数据
      list: [],
      // 状态数据字典
      statusOptions: [],
      // 查询参数
      queryParams: {
        page: 1,
        page_size: 10,
        remote_ip: undefined,
        username: undefined,
        login_type: undefined,
        start_date: undefined,
        end_date: undefined
      },
      start_date: '',
      end_date: '',
      pickerOptions: {
        disabledDate: function disabledDate(time) {
          return time.getTime() > Date.now();
        },
        shortcuts: [{
          text: '今天',
          onClick: function onClick(picker) {
            picker.$emit('pick', new Date());
          }
        }, {
          text: '昨天',
          onClick: function onClick(picker) {
            var date = new Date();
            date.setTime(date.getTime() - 3600 * 1000 * 24);
            picker.$emit('pick', date);
          }
        }, {
          text: '一周前',
          onClick: function onClick(picker) {
            var date = new Date();
            date.setTime(date.getTime() - 3600 * 1000 * 24 * 7);
            picker.$emit('pick', date);
          }
        }]
      }
    };
  },
  created: function created() {
    this.getList(); // this.getDicts('sys_common_status').then(response => {
    //     this.statusOptions = response.data
    // })
  },
  methods: {
    addDateTime: function addDateTime(val) {
      var dateTime = new Date(val);
      dateTime = dateTime.setDate(dateTime.getDate() + 1);
      dateTime = new Date(dateTime);
      return dateTime.toLocaleDateString();
    },

    /** 查询登录日志列表 */
    getList: function getList() {
      var _this = this;

      this.loading = true;

      if (this.start_date && this.end_date) {
        this.queryParams.start_date = this.start_date;
        this.queryParams.end_date = this.addDateTime(this.end_date);
      } else {
        this.queryParams.start_date = undefined;
        this.queryParams.end_date = undefined;
      }

      getLoginList(this.queryParams).then(function (response) {
        _this.list = response.results;

        for (var i in _this.list) {
          _this.list[i]['num'] = parseInt(i) + 1;
        }

        _this.total = response.count;
        _this.loading = false;
      });
    },

    /** 搜索按钮操作 */
    handleQuery: function handleQuery() {
      this.queryParams.page = 1;
      this.getList();
    },

    /** 重置按钮操作 */
    resetQuery: function resetQuery() {
      this.dateRange = [];
      this.start_date = '';
      this.end_date = '';
      this.resetForm('queryForm');
      this.handleQuery();
    },

    /** 转换登录状态为中文 */
    formatLoginType: function formatLoginType(row, column) {
      if (row.login_type === 0) {
        return '登录';
      } else if (row.login_type === 1) {
        return '登出';
      } else if (row.login_type === 2) {
        return '登录失败';
      } else {
        return '位置状态';
      }
    },

    /** 导出按钮操作 */
    handleExport: function handleExport() {
      var _this2 = this;

      // const queryParams = this.queryParams
      this.$confirm('是否确认导出现在的登录日志表单?', '警告', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        _this2.downloadLoading = true;
        import('@/utils/Export2Excel').then(function (excel) {
          var tHeader = ['编号', '用户编号', '用户名称', '登陆IP', '登陆状态', '登陆日期'];
          var filterVal = ['num', 'user_id', 'username', 'remote_ip', 'login_type', 'datetime'];
          var list = _this2.list;
          var data = formatJson(filterVal, list);
          excel.export_json_to_excel({
            header: tHeader,
            data: data,
            filename: '登陆日志',
            autoWidth: true,
            // Optional
            bookType: 'xlsx' // Optional

          });
          _this2.downloadLoading = false;
        });
      });
    }
  }
};
