export default {
  name: 'GroupForm',
  props: {
    form: {
      type: Object,
      default: function() {
        return {
          name: ''
        }
      }
    },
    value: {
      type: String,
      required: true
    }
  },
  data() {
    return {
      rules: {
        name: [
          { required: true, message: '请输入机房名称', trigger: 'blur' },
          { max: 80, message: '长度不能超过80个字符', trigger: 'blur' }
        ]
      }
    }
  },
  methods: {
    submitForm() {
      this.$refs.form.validate((valid) => {
        if (valid) {
          this.$emit('submit', this.form)
        } else {
          return
        }
      })
    },
    cancelForm() {
      this.$emit('cancel')
    }
  }
}
