import bus from '../common/bus';
export default {
    data() {
        return {
            collapse: false,
            items: [
                {
                    icon: 'el-icon-s-home',
                    index: '/dashboard',
                    title: '系统首页'
                },
                {
                    icon: 'el-icon-s-custom',
                    index: 'perm',
                    title: '权限管理',
                    subs: [
                        {
                            index: '/perm/users',
                            title: '用户设置'
                        },
                        {
                            index: '/perm/groups',
                            title: '组设置',
                        }
                    ]
                },
                {
                    icon: 'el-icon-cpu',
                    index: 'redis',
                    title: 'redis面板',
                  subs: [
                    {
                      index: '/redis/management',
                      title: 'redis集群管理'
                    },
                    {
                      index: '/redis/operation',
                      title: 'redis集群控制',
                    }
                  ]
                },
                {
                    icon: 'el-icon-chat-dot-round',
                    index: 'rabbitmq',
                    title: 'rabbitmq面板',
                    subs: [
                        {
                            index: '/rabbitmq/management',
                            title: 'rabbitmq集群管理'
                        }
                    ]
                },
            ]
        };
    },
    computed: {
        onRoutes() {
            return this.$route.path.replace('/', '');
        }
    },
    created() {
        // 通过 Event Bus 进行组件间通信，来折叠侧边栏
        bus.$on('collapse', msg => {
            this.collapse = msg;
            bus.$emit('collapse-content', msg);
        });
    }
};
