export default {
    data: function() {
        return {
            param: {
                username: '',
                password: '',
            },
            rules: {
                username: [{ required: true, message: '请输入用户名', trigger: 'blur' }],
                password: [{ required: true, message: '请输入密码', trigger: 'blur' }],
            },
          loading: false,
        };
    },
    methods: {
        submitForm() {
            this.$refs.login.validate(valid => {
              if (valid) {
                this.loading = true
                this.$store.dispatch('Login', this.param).then(() => {
                  this.loading = false
                  this.$router.push({ path: this.redirect || '/' }).catch(err=>{})
                }).catch(error => {
                  this.loading = false
                  this.$message({
                    type: 'error',
                    message: error.response
                  })
                })
              }else {
                console.log('error submit!!')
                return false
              }
            });
        },
    },
};
