export default {
  name: 'UserList',
  props: {
    values: {
      type: Array,
      default: function() {
        return []
      }
    },
    loading: {
      type: Boolean,
      default: false
    }
  },
  methods: {
    passEdit(user) {
      this.$emit('editPass', user)
    },
    userEdit(user) {
      this.$emit('edit', user)
    },
    userGroups(user) {
      this.$emit('editGroup', user)
    },
    userDelete(user) {
      this.$confirm(`此操作将删除该${user.username}用户记录, 是否继续?`, '删除用户', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        this.$emit('delete', user.id)
      }).catch(() => {
        this.$message({
          type: 'info',
          message: '已取消删除'
        })
      })
    }

  }
}
