import { getServerList } from '@/api/redis_server'
import { getUserList } from '@/api/users'
import { getGroupList } from '@/api/groups'
import { getRabbitmqServerList } from '@/api/rabbitmq_server'
import { mapGetters } from 'vuex'
export default {
    name: 'dashboard',
    data() {
      return {
        redisClusterNum: '',
        rabbitmqClusterNum: '',
        userNum: '',
        groupNum: ''
      }
    },
    // components: {
    // },
  computed: {
    ...mapGetters([
      'sidebar',
      'avatar',
      'username'
    ])
  },
    created() {
      this.fetchData()
    },
    // activated() {
    //     this.handleListener();
    // },
    // deactivated() {
    //     window.removeEventListener('resize', this.renderChart);
    //     bus.$off('collapse', this.handleBus);
    // },
    methods: {
      fetchData() {
        getServerList().then(
            // 获取服务器个数
            res => {
              this.redisClusterNum = res.count
            }
        )
        getUserList(this.params).then(
            // 获取用户数
            res => {
              this.userNum = res.count
            }
        )
        getGroupList(this.params).then(
            // 获取所有用户组
            res => {
              this.groupNum = res.count
            }
        )
          getRabbitmqServerList(this.params).then(
              res => {
                  this.rabbitmqClusterNum = res.count
              }
          )
      }
        }
};
