import { getServerList, showALLKey, showDBSize, getKey, getKeyTTL, delKey, createKey, clusterNode, clusterInfo } from '@/api/redis_server'
// import { getSupplierList } from '@/api/supplier'
// import { getManufactoryList } from '@/api/manufactory'
// import { getIdcList } from '@/api/idc'

export default {
  name: 'redisOperation',
  data() {
    return {
      cluster_options: [],
      value_id: '',
      value_cluster_name: '',
      value_hosts: '',
      cluster_list: [],
      loading: false,
      result_code: '',
      result_msg: '',
      totalNum: 0,
      server: [],
      input_key_for_value: '',
      input_key_for_ttl: '',
      input_key_for_del: '',
      input_key_for_create: '',
      input_value_for_create: '',
      selected_server_id: null,
      params: {
        page: 1,
        keywords: '',
        page_size: 10000
      },
      kwargs: {}
    }
  },
  created() {
    this.fetchData()
    this.value_id = this.$store.state.redis.server_id
  },
  methods: {
    fetchData() {
      getServerList(this.params).then(
          // 获取redis集群列表
          res => {
            this.server = res.results
            this.totalNum = res.count
            //
            this.cluster_list = this.server.map(item => {
              return { id: `${item.id}`, hosts: `${item.hosts}`, cluster_name: `${item.cluster_name}` }
            })

            if (this.$store.state.redis.server_id !== 0) {
              // this.value = this.$store.state.redis.server_id
              this.value_cluster_name = this.cluster_list.find(item => {
                if (parseInt(item.id) === this.$store.state.redis.server_id) {
                  return item
                }
              }).cluster_name
              this.value_hosts = this.cluster_list.find(item => {
                if (parseInt(item.id) === this.$store.state.redis.server_id) {
                  return item
                }
              }).hosts
            }
          },
          err => {
            this.$message({
              type: 'error',
              message: err
            })
          }
      )
    },
    remoteSearchRedisClusterName(query) {
      this.cluster_list = this.server.map(item => {
        return { id: `${item.id}`, hosts: `${item.hosts}`, cluster_name: `${item.cluster_name}` }
      })
      if (query !== '') {
        this.loading = true
        setTimeout(() => {
          this.loading = false
          this.cluster_options = this.cluster_list.filter(item => {
            return item.cluster_name.toLowerCase()
                .indexOf(query.toLowerCase()) > -1
          })
        }, 200)
      } else {
        this.cluster_options = []
      }
    },
    remoteSearchRedisClusterHosts(query) {
      this.cluster_list = this.server.map(item => {
        return { id: `${item.id}`, hosts: `${item.hosts}`, cluster_name: `${item.cluster_name}` }
      })
      if (query !== '') {
        this.loading = true
        setTimeout(() => {
          this.loading = false
          this.cluster_options = this.cluster_list.filter(item => {
            return item.hosts.toLowerCase()
                .indexOf(query.toLowerCase()) > -1
          })
        }, 200)
      } else {
        this.cluster_options = []
      }
    },
    setSelectedClusterName() {
      this.value_cluster_name = this.cluster_options.find(item => {
        if (item.hosts === this.value_hosts) {
          return item
        }
      }).cluster_name
    },
    setSelectedHosts() {
      // this.cluster_options = this.server.map(i => {
      //   return { value: `${i.id}`, label: `${i.cluster_name}` }
      // })
      this.value_hosts = this.cluster_options.find(item => {
        if (item.cluster_name === this.value_cluster_name) {
          return item
        }
      }).hosts
    },
    handlershowALLKey() {
      this.value_id = this.cluster_list.find(item => {
        if (item.cluster_name === this.value_cluster_name) {
          return item
        }
      }).id
      showALLKey(this.value_id).then(
          // 获取redis集群列表
          res => {
            let str_key = '\n'
            for (var i in res.msg) {
              str_key = str_key + res.msg[i] + '\n'
            }
            this.result_code = '返回code：' + res.code
            this.result_msg = '返回结果：' + str_key
          },
          err => {
            this.$message({
              type: 'error',
              message: err
            })
          }
      )
    },
    handlershowDBSize() {
      const obj_item = this.cluster_list.find(item => {
        if (item.cluster_name === this.value_cluster_name) {
          return item
        }
      })
      this.value_id = obj_item.id
      showDBSize(this.value_id).then(
          // 获取redis集群列表
          res => {
            let msg = "\n"
            if ( res.msg instanceof Object) {
              for(var key in  res.msg){
                msg = msg + key + ":" + '\n'
                msg = msg + res.msg[key] + '\n'
              }
              // msg = JSON.stringify(res.msg).toString()
            } else {
              msg = res.msg
            }
            this.result_code = '返回code：' + res.code
            this.result_msg = '返回结果：' + msg
          },
          err => {
            this.$message({
              type: 'error',
              message: err
            })
          }
      )
    },
    handleGetKey() {
      this.value_id = this.cluster_list.find(item => {
        if (item.cluster_name === this.value_cluster_name) {
          return item
        }
      }).id
      this.kwargs.id = this.value_id
      this.kwargs.key = this.input_key_for_value
      getKey(this.kwargs).then(
          // 获取redis集群列表
          res => {
            this.result_code = '返回code：' + res.code
            this.result_msg = '返回结果：\n' + res.msg
          },
          err => {
            this.$message({
              type: 'error',
              message: err
            })
          }
      )
    },
    handleGetKeyTTL() {
      this.value_id = this.cluster_list.find(item => {
        if (item.cluster_name === this.value_cluster_name) {
          return item
        }
      }).id
      this.kwargs.id = this.value_id
      this.kwargs.key = this.input_key_for_ttl
      getKeyTTL(this.kwargs).then(
          // 获取redis集群列表
          res => {
            this.result_code = '返回code：' + res.code
            this.result_msg = '返回结果：' + res.msg
          },
          err => {
            this.$message({
              type: 'error',
              message: err
            })
          }
      )
    },
    handleDelKey() {
      this.value_id = this.cluster_list.find(item => {
        if (item.cluster_name === this.value_cluster_name) {
          return item
        }
      }).id
      this.kwargs.id = this.value_id
      this.kwargs.key = this.input_key_for_del
      delKey(this.kwargs).then(
          // 获取redis集群列表
          res => {
            this.result_code = '返回code：' + res.code
            if (parseInt(res.msg) === 1) {
              this.result_msg = '返回结果：成功删除'
            }
            if (parseInt(res.msg) === 0) {
              this.result_msg = '返回结果：不存在该键'
            }
            if (parseInt(res.msg) > 1) {
              this.result_msg = '返回结果：成功删除' + res.msg + '个键'
            }
          },
          err => {
            this.$message({
              type: 'error',
              message: err
            })
          }
      )
    },
    handleCreateKey() {
      this.value_id = this.cluster_list.find(item => {
        if (item.cluster_name === this.value_cluster_name) {
          return item
        }
      }).id
      this.kwargs.id = this.value_id
      this.kwargs.key = this.input_key_for_create
      this.kwargs.value = this.input_value_for_create
      createKey(this.kwargs).then(
          // 获取redis集群列表
          res => {
            this.result_code = '返回结果：' + res
            this.result_msg = ''
          },
          err => {
            this.$message({
              type: 'error',
              message: err
            })
          }
      )
    },
    handlershowClusterInfo() {
      this.value_id = this.cluster_list.find(item => {
        if (item.cluster_name === this.value_cluster_name) {
          return item
        }
      }).id
      clusterInfo(this.value_id).then(
              // 获取cluster info
              res => {
                let msg = ""
                if ( res.msg instanceof Object) {
                  for(var key in  res.msg){
                    msg = msg + '\n'
                    msg = msg + "集群节点" + key + ":" + '\n'
                    if (res.msg[key] instanceof Object){
                      for (var value_key in res.msg[key]) {
                        msg = msg + value_key + ":" + '\n'
                        msg = msg + res.msg[key][value_key] + '\n'
                      }
                    } else {
                      msg = msg + res.msg[key] + '\n'
                    }

                  }
                  // msg = JSON.stringify(res.msg).toString()
                } else {
                  msg = res.msg
                }
                this.result_code = '返回code：' + res.code
                this.result_msg = '返回结果：' + msg
              },
              err => {
                this.$message({
                  type: 'error',
                  message: err
                })
              }
      )
    },
    handlershowClusterNode() {
      this.value_id = this.cluster_list.find(item => {
        if (item.cluster_name === this.value_cluster_name) {
          return item
        }
      }).id
      clusterNode(this.value_id).then(
              // 获取cluster info
              res => {
                let msg = ""
                if (res.code === 200){
                  for (var i in res.msg)
                  {
                    msg = msg + '\n'
                    msg = msg + "集群节点" + res.msg[i]['host'] + ":" + res.msg[i]['port'] + "\n"
                    msg = msg + 'id:' + res.msg[i]['id'] + '\n'
                    msg = msg + 'cluster-bus-port:' + res.msg[i]['cluster-bus-port'] + '\n'
                    msg = msg + 'flags:' + res.msg[i]['flags'] + '\n'
                    msg = msg + 'master:' + res.msg[i]['master'] + '\n'
                    msg = msg + 'ping-sent:' + res.msg[i]['ping-sent'] + '\n'
                    msg = msg + 'pong-recv:' + res.msg[i]['pong-recv'] + '\n'
                    msg = msg + 'link-state:' + res.msg[i]['link-state'] + '\n'
                    msg = msg + 'migrations:' + res.msg[i]['migrations'] + '\n'
                  }
                } else {
                  msg = msg + res.msg
                }
                this.result_code = '返回code：' + res.code
                this.result_msg = '返回结果：' + msg
              },
              err => {
                this.$message({
                  type: 'error',
                  message: err.response
                })
              }
      )
    }
  }
}
